let UserMainBalance;




function init() {

    console.log("Initializing example");
    if (typeof window.ethereum !== 'undefined' || (typeof window.web3 !== 'undefined')) {
        // console.log("WalletConnectProvider is", WalletConnectProvider);
        // console.log("Fortmatic is", Fortmatic);
        console.log("window.web3 is", window.web3, "window.ethereum is", window.ethereum);

        const providerOptions = {
        };

        web3Modal = new Web3Modal({
            cacheProvider: false, // optional
            providerOptions, // required
            disableInjectedProvider: false, // optional. For MetaMask / Brave / Opera.
        });

        console.log("Web3Modal instance is", web3Modal);

    } else {
        Swal.fire({
            title: 'Error!',
            text: 'Wallet doesn\'t exist in your web Browser!',
            icon: 'error',
            confirmButtonText: 'Cancel'
        });
    }
}


/**
  * Connect wallet button pressed.
  */
async function onConnect() {

    console.log("Opening a dialog", web3Modal);
    try {
        provider = await web3Modal.connect();
    } catch (e) {
        console.log("Could not get a wallet connection", e);
        return;
    }

    // Subscribe to accounts change
    provider.on("accountsChanged", (accounts) => {
        fetchAccountData();
    });

    // Subscribe to chainId change
    provider.on("chainChanged", (chainId) => {
        fetchAccountData();
    });

    // Subscribe to networkId change
    provider.on("networkChanged", (networkId) => {
        fetchAccountData();
    });

    await refreshAccountData();
}

/**
 * Fetch account data for UI when
 * - User switches accounts in wallet
 * - User switches networks in wallet
 * - User connects wallet initially
 */
async function refreshAccountData() {

    // If any current data is displayed when
    // the user is switching acounts in the wallet
    // immediate hide this data
    // document.querySelector("#connected").style.display = "none";
    // document.querySelector("#prepare").style.display = "block";

    // Disable button while UI is loading.
    // fetchAccountData() will take a while as it communicates
    // with Ethereum node via JSON-RPC and loads chain data
    // over an API call.
    // document.querySelector("#btn-connect").setAttribute("disabled", "disabled")
    await fetchAccountData(provider);
    // document.querySelector("#btn-connect").removeAttribute("disabled")
}

/**
 * Kick in the UI action after Web3modal dialog has chosen a provider
 */
async function fetchAccountData() {

    // Get a Web3 instance for the wallet
    const web3 = new Web3(provider);

    console.log("Web3 instance is", web3);

    // Get connected chain id from Ethereum node
    const chainId = await web3.eth.getChainId();
    // Load chain information over an HTTP API
    const chainData = evmChains.getChain(chainId);
    // document.querySelector("#network-name").textContent = chainData.name;

    // Get list of accounts of the connected wallet
    const accounts = await web3.eth.getAccounts();




    // MetaMask does not give you all accounts, only the selected account
    console.log("Got accounts", accounts);
    selectedAccount = accounts[0];



    var MainBalance = await web3.eth.getBalance(selectedAccount); //Will give value in.

    console.log('=======================================================');
    console.log(MainBalance);
    console.log('=======================================================');

    MainBalance = parseFloat(MainBalance) / 10 ** 18;

    bnbBalnce = MainBalance;

    let tokencontractBUSD = new web3.eth.Contract(TOKEN_ABI, TOKEN_ADDRESS, { from: selectedAccount });
    let balanceBUSD = await tokencontractBUSD.methods.balanceOf(selectedAccount).call();

    balanceBUSD = parseFloat(balanceBUSD) / 10 ** 18;
    busdBalnce = balanceBUSD;



    let tokencontractFDN = new web3.eth.Contract(FDNabi, FDNAddress, { from: selectedAccount });
    let balanceFDN = await tokencontractFDN.methods.balanceOf(selectedAccount).call();

    balanceFDN = parseFloat(balanceFDN) / 10 ** 18;

    $(document).find('span#walletBalance').html('<span class="text-success" >  BNB : ' + MainBalance + ' BUSD : ' + balanceBUSD + ' ZNX : ' + balanceFDN + '</span>');

    // document.querySelector("#selected-account").textContent = selectedAccount;

    // Get a handl
    // const template = document.querySelector("#template-balance");
    // const accountContainer = document.querySelector("#accounts");

    // Purge UI elements any previously loaded accounts
    // accountContainer.innerHTML = '';

    // Go through all accounts and get their ETH balance
    const rowResolvers = accounts.map(async (address) => {
        const balance = await web3.eth.getBalance(address);
        // ethBalance is a BigNumber instance
        // https://github.com/indutny/bn.js/
        const ethBalance = web3.utils.fromWei(balance, "ether");
        const humanFriendlyBalance = parseFloat(ethBalance).toFixed(4);
        // Fill in the templated row and put in the document
        //   const clone = template.content.cloneNode(true);
        //   clone.querySelector(".address").textContent = address;
        //   clone.querySelector(".balance").textContent = humanFriendlyBalance;
        //   accountContainer.appendChild(clone);
    });

    // Because rendering account does its own RPC commucation
    // with Ethereum node, we do not want to display any results
    // until data for all accounts is loaded
    await Promise.all(rowResolvers);

    // Display fully loaded UI for wallet data
    // document.querySelector("#prepare").style.display = "none";
    // document.querySelector("#connected").style.display = "block";
}



